document.querySelector('#nik').addEventListener('paste',pasteTest);
function pasteTest(event){
    window.setTimeout(() => {
        var characters =event.target.value;
        window.setTimeout(() => {
            if(!(/^\d+$/.test(characters))){
                event.target.value = event.target.value.replace(/\D/g, '');
            }
        });
    });
}

$('#submit').click(function() {
    var nik     = $('#nik').val();
    var role    = $('#role').val();
    var pwd     = $('#pwd').val();

    if(nik.length < 16){
        swal("Oops!", "NIK kurang dari 16 character", "error");
        return false;
    }else if(nik.length > 16){
        swal("Oops!", "NIK melebihi dari 16 character", "error");
        return false;
    }else if(!role){
        swal("Oops!", "Role tidak boleh kosong", "error");
        return false;
    }else if(!pwd){
        swal("Oops!", "Password tidak boleh kosong", "error");
        return false;
    }else{
        $('#loader').removeClass('hidden');
        setTimeout(function() { sendjwt(); }, 2000);
    }
    // $('#loader').removeClass('hidden');
    // setTimeout(function() { sendjwt(); }, 2000);
});

var getUrl = window.location;
var baseUrl = getUrl .protocol + "//" + getUrl.host + "/" + getUrl.pathname.split('/')[1];

function sendjwt(){
    var nik     = $('#nik').val();
    var role    = $('#role').val();
    var pwd     = md5($('#pwd').val());

    $.ajax({
        type: 'POST',
        dataType: 'json',
        url: baseUrl+ '/src/php/output.php/createjwt',
        data :  {
                    nik     : nik,
                    role    : role,
                    pwd     : pwd,
                },
        success: function(result){
            $('#loader').addClass('hidden')
            console.log(result);

            $('#nik').val('');
            $('#role').val('');
            $('#pwd').val('');

            if(result.status == 1){

                $('#result').empty();
                $('#result').css('display','block');
                $('#result').append("<p style='word-break: break-all !important'>Result Authenticate APP<br>ID = "+result.id+"<br>NIK = "+result.nik+"<br>Role = "+result.role+"<br>Token JWT "+result.jwt+"</p>");
                $('#result').append("<p style='word-break: break-all !important' id='result_fetch_app'></p>");

                fetch_app();
            }else{
                $('#result').empty();
                swal("Oops!", "Invalid JWT", "error");
                $('#result').css('display','none');

            }
        },
        error: function(xhr) {
            $('#loader').addClass('hidden');
            swal("Oops!", "Invalid JWT", "error");
        }
    });
}

function fetch_app(){
    var url = "https://60c18de74f7e880017dbfd51.mockapi.io/api/v1/jabar-digital-services/product";
    
    var xhr = new XMLHttpRequest();
    xhr.open("GET", url);
    
    xhr.setRequestHeader("Accept", "application/json");
    xhr.setRequestHeader("Content-Type", "application/x-www-form-urlencoded");
    // xhr.setRequestHeader("Content-Length", "0");
    
    xhr.onreadystatechange = function () {
    if (xhr.readyState === 4) {
        console.log(xhr);
        var result  = xhr.responseText;
        var data    = $.parseJSON(result);
        currency(data[0].price, data[0]);
    }};
    
    xhr.send();    
}

function currency(jumlah, data){
    $('#loader').removeClass('hidden');

    $.ajax({
        type: 'POST',
        dataType: 'json',
        url: baseUrl+ '/src/php/output.php/fetch_app',
        data :  {
                    jumlah     : jumlah
                },
        success: function(result){
            $('#loader').addClass('hidden');
            console.log(result);
            if(result.success == true){

                $('#result_fetch_app').empty();
                $('#result_fetch_app').css('display','block');
                $('#result_fetch_app').append(
                                            "FETCH APP"+
                                            "ID = "+data.id+"<br>"+
                                            "Department = "+data.department+"<br>"+
                                            "Product = "+data.product+"<br>"+
                                            "Price (USD) = "+data.price+"<br>"+
                                            "Currency (IDR) = "+result.result+"<br>"
                                            );
            }else{
                swal("Oops!", "Invalid JWT", "error");
                $('#result_fetch_app').empty();
                $('#result_fetch_app').css('display','none');
            }
        },
        error: function(xhr) {
            $('#loader').addClass('hidden');
            swal("Oops!", "Invalid JWT", "error");
        }
    });
}